import{_ as n,c as e,o as i,d as l}from"./app.9c06079b.js";const h='{"title":"Linux\u542F\u52A8Nginx\u670D\u52A1","description":"Linux\u542F\u52A8Nginx\u670D\u52A1\u8BE6\u7EC6\u6559\u7A0B","frontmatter":{"title":"Linux\u542F\u52A8Nginx\u670D\u52A1","head":[["meta",{"name":"description","content":"Linux\u542F\u52A8Nginx\u670D\u52A1\u8BE6\u7EC6\u6559\u7A0B"}],["meta",{"name":"keywords","content":"Linux Nginx Ubuntu CentOs \u5EFA\u7AD9"}]]},"headers":[{"level":2,"title":"\u542F\u52A8linux\u7CFB\u7EDF","slug":"linux"},{"level":3,"title":"windows\u4E0B\u5B89\u88C5linux","slug":"windowslinux"},{"level":3,"title":"\u865A\u62DF\u673A\u5B89\u88C5linux\u7CFB\u7EDF","slug":"linux-1"},{"level":2,"title":"\u5B89\u88C5Nginx","slug":"nginx"},{"level":2,"title":"\u542F\u52A8Nginx","slug":"nginx-1"},{"level":2,"title":"\u914D\u7F6Enginx.config","slug":"nginxconfig"},{"level":2,"title":"\u4F20\u8F93\u6587\u4EF6","slug":"\u4F20\u8F93\u6587\u4EF6"},{"level":2,"title":"\u9A8C\u8BC1\u914D\u7F6E\u662F\u5426\u6210\u529F","slug":"-1"}],"relativePath":"front-end/linux/index.md","lastUpdated":1642523331220}',t={},s=l(`<h2 id="linux" tabindex="-1"><a class="header-anchor" href="#linux">#</a> \u542F\u52A8linux\u7CFB\u7EDF</h2><h3 id="windowslinux" tabindex="-1"><a class="header-anchor" href="#windowslinux">#</a> windows\u4E0B\u5B89\u88C5linux</h3><ul><li>windows\u6709\u5185\u7F6E\u7684linux\u7CFB\u7EDF\uFF0C\u5982\u4F55\u5B89\u88C5\uFF0C\u8BF7\u81EA\u884C\u767E\u5EA6</li><li>\u5B89\u88C5\u795E\u5668\u5DE5\u5177\uFF1Axshell\u548Cxftp\uFF08\u8BF7\u81EA\u884C\u767E\u5EA6\u4E0B\u8F7D\u7834\u89E3\u7248<code>\u975E\u7834\u89E3\u7248\u6536\u8D39\u{1F614}</code>\uFF09</li><li>\u786E\u8BA4\u5B89\u88C5\u7684\u662FUbuntu\u8FD8\u662FCentOS\uFF1A <ul><li>xshell\u8FD0\u884C<code>yum -help</code>\uFF1A\u6709\u7ED3\u679C\u5C31\u662FCentOS <ul><li>\u67E5\u770BCentOS\u7248\u672C<code>cat /etc/redhat-release</code></li></ul></li><li>xshell\u8FD0\u884C<code>apt -help</code>\uFF1A\u6709\u7ED3\u679C\u5C31\u662FUbuntu</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>\u8981\u8BF4\u660E\u7684\u4E00\u70B9\u662FUbuntu\u662F\u754C\u9762\u64CD\u4F5C\u6A21\u5F0F\uFF0C\u800CCentOS\u662F\u547D\u4EE4\u884C\u64CD\u4F5C\u6A21\u5F0F</p></div><h3 id="linux-1" tabindex="-1"><a class="header-anchor" href="#linux-1">#</a> \u865A\u62DF\u673A\u5B89\u88C5linux\u7CFB\u7EDF</h3><p>\u5B89\u88C5Ubuntu\u6216\u662FCentOS\u955C\u50CF\uFF08\u8BF7\u81EA\u884C\u767E\u5EA6\u4E0B\u8F7D\u955C\u50CF\uFF09</p><p><strong>\u4EE5\u4E0B\u662Fwindows\u4E0B\u7684<code>CentOS</code>\u7CFB\u7EDF\u8BB2\u89E3</strong></p><h2 id="nginx" tabindex="-1"><a class="header-anchor" href="#nginx">#</a> \u5B89\u88C5Nginx</h2><ul><li>\u79BB\u7EBF\u5B89\u88C5<a href="http://nginx.org/packages/" target="_blank" rel="noopener noreferrer">\u5B98\u7F51\u4E0B\u8F7D</a><ul><li><code>http://nginx.org/packages/centos/7/x86_64/RPMS/</code>\uFF08\u6211\u7684CentOS\u662F7\u7248\u672C\uFF09\uFF0C\u7136\u540E\u9009\u62E9\u4E00\u4E2Arpm\u6587\u4EF6\u4E0B\u8F7D\uFF08\u6211\u4E00\u822C\u90FD\u4E60\u60EF\u4E0B\u8F7D\u6700\u65B0\u7248\u6216\u662F\u6B21\u6700\u65B0\u7248\uFF09</li><li>\u8FD0\u884C\u8FD9\u4E2A\u547D\u4EE4<code>sudo yum install -y \u4F60\u7684rpm\u5305</code></li></ul></li><li>\u5728\u7EBF\u5B89\u88C5\uFF1A\u8BF7\u81EA\u884C\u767E\u5EA6</li></ul><h2 id="nginx-1" tabindex="-1"><a class="header-anchor" href="#nginx-1">#</a> \u542F\u52A8Nginx</h2><p>\u542F\u52A8Nginx\u5E76\u8BBE\u7F6E\u5F00\u673A\u542F\u52A8</p><div class="language-bash"><pre><code><span class="token function">sudo</span> <span class="token function">service</span> nginx start
<span class="token comment">#\u6216\u8005</span>
<span class="token function">sudo</span> systemctl start nginx.service
<span class="token function">sudo</span> systemctl <span class="token builtin class-name">enable</span> nginx.service
</code></pre></div><h2 id="nginxconfig" tabindex="-1"><a class="header-anchor" href="#nginxconfig">#</a> \u914D\u7F6Enginx.config</h2><p>\u9ED8\u8BA4\u5B89\u88C5\u5219\u914D\u7F6E\u6587\u4EF6\u4E00\u822C\u662F\u8FD9\u4E2A\uFF1A<code>/etc/nginx/nginx.conf</code></p><p>root\u7528\u6237\u542F\u52A8nginx\u9ED8\u8BA4\u76D1\u542C80\u7AEF\u53E3</p><p><a href="./nginx.html">nginx\u914D\u7F6E\u6559\u7A0B</a></p><h2 id="" tabindex="-1"><a class="header-anchor" href="#">#</a> \u4F20\u8F93\u6587\u4EF6</h2><p>xft\u767B\u5F55\u7136\u540E\u76F4\u63A5\u9F20\u6807\u62D6\u62FD\u5373\u53EF</p><h2 id="-1" tabindex="-1"><a class="header-anchor" href="#-1">#</a> \u9A8C\u8BC1\u914D\u7F6E\u662F\u5426\u6210\u529F</h2><p>\u6D4F\u89C8\u5668\u8F93\u5165\u7F51\u5740\u3001\u56DE\u8F66\u5373\u53EF\u67E5\u770B\u662F\u5426\u51FA\u73B0\u9875\u9762</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>\u67E5\u770BNginx\u7248\u672C\uFF1Anginx -v</li><li>\u67E5\u770BNginx\u542F\u52A8\u72B6\u6001\uFF1A <ul><li>sudo service nginx status</li><li>sudo systemctl status nginx.service</li></ul></li><li>\u67E5\u770BNginx\u4F4D\u7F6E\uFF1Asudo whereis nginx</li><li>\u91CD\u542FNginx\uFF1Anginx -s reload</li><li>\u6BCF\u6B21\u4FEE\u6539\u5B8C\u914D\u7F6E\u6587\u4EF6\u4E4B\u540E\u8BB0\u5F97\u91CD\u542Fnginx</li></ul></div>`,21),a=[s];function o(c,d,x,u,r,g){return i(),e("div",null,a)}var f=n(t,[["render",o]]);export{h as __pageData,f as default};
